(ns advent-2022.day-5
  (:require [clojure.string :as string]))

(def matrix-invert (partial apply mapv vector))

(defn parse-stacks
  [stack-string]
  (->> (string/split-lines stack-string)
       (drop-last)                                          ; drop bucket numbers
       (map (fn [row]                                       ; get just the letters
              (map #(nth % 1) (partition-all 4 row))))
       (matrix-invert)                                      ; flip from list of rows to list of columns
       (map (fn [stack] (drop-while #(= % \space) stack)))  ; remove spaces at the tops of columns
       (zipmap (rest (range)))))                            ; create dictionary indexed by numbers starting at 1

(defn parse-instructions
  [instruction-string]
  (map #(map (fn [str] (Integer/parseInt str))
             (rest (re-matches #"move (\d+) from (\d+) to (\d+)" %)))
       (string/split-lines instruction-string)))

(defn parse-input
  [input-string]
  (let [[stack-string instruction-string] (string/split input-string #"\n\n")]
    [(parse-stacks stack-string) (parse-instructions instruction-string)]))

(defn move-crates
  [stacks [num from to] reverse-moved-crates?]
  (let [crates-to-place (cond-> (take num (get stacks from))
                                reverse-moved-crates? (reverse))]
    (-> stacks
        (assoc to (concat crates-to-place (get stacks to)))
        (assoc from (drop num (get stacks from))))))

(defn run-commands
  [input-string reverse-moved-crates?]
  (let [[stacks instructions] (parse-input input-string)]
    (loop [stacks stacks
           [instruction & remaining-instructions] instructions]
      (if instruction
        (recur (move-crates stacks instruction reverse-moved-crates?) remaining-instructions)
        (string/join (map (comp first stacks) (range 1 (inc (count stacks)))))))))

(defn part-one [input-string] (run-commands input-string true))

(defn part-two [input-string] (run-commands input-string false))

