(ns advent-2022.day-6)

(defn characters-before-payload
  [marker-length string]
  (->> (partition marker-length 1 string)
       (take-while #(not= marker-length (count (distinct %))))
       (count)
       (+ marker-length)))

(defn part-one [input-string] (characters-before-payload 4 input-string))
(defn part-two [input-string] (characters-before-payload 14 input-string))

