(ns advent-2022.day-7
  (:require [clojure.string :as string]))

(defn build-tree
  [input-string]
  (loop [[line & remaining-lines] (string/split-lines input-string)
         path []
         tree {}]
    (cond
      (not line) tree
      (string/starts-with? line "$ cd ..") (recur remaining-lines (pop path) tree)
      (string/starts-with? line "$ cd") (recur remaining-lines (conj path (last (string/split line #"\s"))) tree)
      (string/starts-with? line "$") (recur remaining-lines path tree)
      (string/starts-with? line "dir") (recur remaining-lines path tree)
      :else (let [[size-string file-name] (string/split line #"\s")]
              (recur remaining-lines path (assoc-in tree (conj path file-name) (Integer/parseInt size-string)))))))

(defn directory-sizes
  ([tree]
   (:dir-sizes (directory-sizes (get tree "/") ["/"])))
  ([tree path]
   (let [sub-dirs (mapv (fn [[key subtree]]
                          (if (number? subtree)
                            {:size subtree :dir-sizes {}}
                            (directory-sizes subtree (conj path key))))
                        tree)
         sum-size (reduce + (map :size sub-dirs))]
     {:size      sum-size
      :dir-sizes (assoc (apply merge (map :dir-sizes sub-dirs)) path sum-size)})))

(defn part-one
  [input-string]
  (->> (build-tree input-string)
       (directory-sizes)
       (vals)
       (filter #(<= % 100000))
       (reduce +)))

(defn part-two
  [input-string total-space required-empty-space]
  (let [sizes (directory-sizes (build-tree input-string))
        space-to-clear (- required-empty-space (- total-space (get sizes ["/"])))]
    (apply min (filter #(>= % space-to-clear) (vals sizes)))))

